import{_ as a,c as l,o as e,af as i}from"./chunks/framework.BMktmvug.js";const p=JSON.parse('{"title":"密钥管理","description":"","frontmatter":{},"headers":[],"relativePath":"docs/manage/keys/index.md","filePath":"docs/manage/keys/index.md"}'),o={name:"docs/manage/keys/index.md"};function r(d,t,n,s,h,c){return e(),l("div",null,t[0]||(t[0]=[i('<h1 id="密钥管理" tabindex="-1">密钥管理 <a class="header-anchor" href="#密钥管理" aria-label="Permalink to &quot;密钥管理&quot;">​</a></h1><p>密钥管理功能帮助您安全地存储和管理 SSH 密钥，支持多种密钥类型和安全的存储方式。</p><h2 id="功能概述" tabindex="-1">功能概述 <a class="header-anchor" href="#功能概述" aria-label="Permalink to &quot;功能概述&quot;">​</a></h2><p>密钥管理模块提供以下核心功能：</p><ul><li><strong>添加密钥</strong> - 支持多种方式导入密钥</li><li><strong>编辑密钥</strong> - 修改密钥信息和配置</li><li><strong>删除密钥</strong> - 安全移除不需要的密钥</li></ul><h2 id="添加密钥" tabindex="-1">添加密钥 <a class="header-anchor" href="#添加密钥" aria-label="Permalink to &quot;添加密钥&quot;">​</a></h2><h3 id="方式一-文件导入" tabindex="-1">方式一：文件导入 <a class="header-anchor" href="#方式一-文件导入" aria-label="Permalink to &quot;方式一：文件导入&quot;">​</a></h3><ol><li><p><strong>拖拽导入</strong></p><ul><li>将私钥文件直接拖拽到指定区域</li><li>系统自动识别密钥格式和类型</li></ul></li><li><p><strong>文件选择</strong></p><ul><li>点击&quot;选择文件&quot;按钮</li><li>浏览并选择私钥文件</li><li>支持 <code>.pem</code>、<code>.key</code>、<code>.rsa</code> 等格式</li></ul></li></ol><h3 id="方式二-手动输入" tabindex="-1">方式二：手动输入 <a class="header-anchor" href="#方式二-手动输入" aria-label="Permalink to &quot;方式二：手动输入&quot;">​</a></h3><p>在手动添加密钥时，需要填写以下信息：</p><table tabindex="0"><thead><tr><th>字段</th><th>说明</th><th>必填</th></tr></thead><tbody><tr><td><strong>密钥名称</strong></td><td>用于标识密钥的友好名称</td><td>✅</td></tr><tr><td><strong>私钥内容</strong></td><td>完整的私钥文本内容</td><td>✅</td></tr><tr><td><strong>公钥内容</strong></td><td>对应的公钥内容（可选）</td><td>❌</td></tr><tr><td><strong>私钥密码</strong></td><td>私钥的密码短语（如有）</td><td>❌</td></tr></tbody></table><h2 id="密钥管理操作" tabindex="-1">密钥管理操作 <a class="header-anchor" href="#密钥管理操作" aria-label="Permalink to &quot;密钥管理操作&quot;">​</a></h2><h3 id="编辑密钥" tabindex="-1">编辑密钥 <a class="header-anchor" href="#编辑密钥" aria-label="Permalink to &quot;编辑密钥&quot;">​</a></h3><ul><li>点击密钥列表中的&quot;编辑&quot;按钮</li><li>修改密钥名称或更新密钥内容</li><li>保存更改后立即生效</li></ul><h3 id="删除密钥" tabindex="-1">删除密钥 <a class="header-anchor" href="#删除密钥" aria-label="Permalink to &quot;删除密钥&quot;">​</a></h3><ul><li>选择要删除的密钥</li><li>确认删除操作</li><li>密钥将被永久移除</li></ul><div class="warning custom-block"><p class="custom-block-title">注意事项</p><p>删除密钥前请确保：</p><ul><li>该密钥未被其他服务使用</li><li>已备份重要的密钥信息</li><li>了解删除操作的不可逆性</li></ul></div>',17)]))}const b=a(o,[["render",r]]);export{p as __pageData,b as default};
